import React, { useEffect, useState } from "react";
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
} from "@mui/material";
import { Link, Route, useNavigate } from "react-router-dom";
import {
  deleteWishlist,
  getAllWishlists,
  postWishlist,
} from "../../api/wishlistApi.js";
import axios from "axios";
import DeleteIcon from "./Trash_font_awesome.svg.png";
import Button from "@mui/material/Button/index.js";
import { spacing } from "@mui/system";

const url = "http://localhost:8000/api";

class WishlistList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      nameValue: "",
      ageValue: 0,
    };
  }

  componentDidMount() {
    let wishlists = getAllWishlists();
    new Promise((resolve) => {
      return resolve(wishlists);
    }).then((res) => this.setState({ data: res }));
  }

  /*
  getAllWishlists = async () => {
    try {
      const response = await axios.get(`${url}/getAll`);
      this.setState({ data: response.data });
    } catch (error) {
      console.error("Error fetching wishlists:", error.message);
    }
  };
  */

  handleDelete = (itemId) => {
    deleteWishlist(itemId);
    window.location.reload();
  };
  handleNameChange = (value) => {
    this.setState({ nameValue: value });
  };

  handleAgeChange = (value) => {
    this.setState({ ageValue: value });
  };

  handleAddWishlist = (name, age) => {
    postWishlist(name, age);
    window.location.reload();
  };
  render() {
    const data = this.state.data;

    return (
      <TableContainer component={Paper}>
        <Button
          variant="contained"
          sx={{ m: 2 }}
          onClick={() =>
            this.handleAddWishlist(this.state.nameValue, this.state.ageValue)
          }
        >
          Add Wishlist
        </Button>
        <TextField
          label="Name"
          variant="outlined"
          onChange={(e) => this.handleNameChange(e.target.value)}
          sx={{ m: 2 }}
        />
        <TextField
          id="outlined-number"
          label="Age"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
          onChange={(e) => this.handleAgeChange(e.target.value)}
          sx={{ m: 2 }}
        />
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Age</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data && data.length > 0 ? (
              data.map((item) => (
                <TableRow key={item._id}>
                  <TableCell component={Link} to={`/${item._id}`}>
                    {item.name}
                  </TableCell>
                  <TableCell>{item.age}</TableCell>

                  <TableCell>
                    <IconButton
                      aria-label="delete"
                      onClick={() => this.handleDelete(item._id)}
                      style={{ textDecoration: "none", cursor: "pointer" }}
                    >
                      <img
                        src={DeleteIcon}
                        alt="logo"
                        style={{ width: "20px" }}
                      />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={3}>Loading...</TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </TableContainer>
      // <List>
      //   {items.map((item) => (
      //     <ListItem key={item.id}>
      //       <ListItemText
      //         primary={`${item.firstname} ${item.lastname} | ${item.year}`}
      //         // Add more ListItemText properties as needed
      //       />
      //       <ListItemSecondaryAction>
      //         <IconButton
      //           edge="end"
      //           aria-label="delete"
      //           onClick={() => handleDelete(item.id)}
      //         >
      //           <DeleteIcon />
      //         </IconButton>
      //       </ListItemSecondaryAction>
      //     </ListItem>
      //   ))}
      // </List>
    );
  }
}

export default WishlistList;
