import * as React from "react";
import TextField from "@mui/material/TextField/index.js";
import Button from "@mui/material/Button/index.js";
import Dialog from "@mui/material/Dialog/index.js";
import DialogActions from "@mui/material/DialogActions/index.js";
import DialogContent from "@mui/material/DialogContent/index.js";
import DialogContentText from "@mui/material/DialogContentText/index.js";
import DialogTitle from "@mui/material/DialogTitle/index.js";
import {
  deleteWishlist,
  getAllWishlists,
  postWishlist,
} from "../../api/wishlistApi.js";

export default function PostDialog() {
  const [open, setOpen] = this.useState(false);
  const [year, setYear] = this.useState(2023);
  const [child, setChild] = this.useState({
    firstname: "",
    lastname: "",
    age: 0,
  });
  const [items, setItems] = this.useState([{ name: "", currentprice: 0 }]);

  const handleAddWishlist = () => {
    const formData = {
      year: year,
      child: { ...child },
      items: [...items],
    };
    postWishlist(formData);
    handleClose();
    window.location.reload();
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const handleAddItem = () => {
    setItems([...items, { name: "", currentprice: 0 }]);
  };
  const handleRemoveItem = (id) => {
    setItems(items.filter((item) => item.id !== id));
  };

  const handleAdd = () => {
    handleAddWishlist();
  };

  return (
    <React.Fragment>
      <Button variant="contained" sx={{ m: 2 }} onClick={handleClickOpen}>
        Add Wishlist
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here.
            We will send updates occasionally.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleAdd}>Add</Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
}
