import React, { useState, useEffect } from "react";
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from "@mui/material";
import {
  deleteWishlist,
  getAllWishlists,
  postWishlist,
  updateWishlist,
} from "../../api/wishlistApi.js";

export default function UpdateDialog(props) {
  const [open, setOpen] = React.useState(false);
  const [firstname, setFirstname] = React.useState("");
  const [lastname, setLastname] = React.useState("");
  const [age, setAge] = React.useState();
  const [year, setYear] = React.useState();

  const handleUpdateWishlist = () => {
    console.log(props.item);
    let updatedChild = {
      firstname: firstname,
      lastname: lastname,
      age: age,
    };

    updateWishlist(props.item._id, year, updatedChild, props.item.items);
    window.location.reload();
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleUpdate = () => {
    handleUpdateWishlist();
    handleClose();
  };

  const wishlistItem = props.item;
  return (
    <React.Fragment>
      <Button variant="outlined" sx={{ m: 2 }} onClick={handleClickOpen}>
        Update Wishlist
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here.
            We will send updates occasionally.
          </DialogContentText>
          <TextField
            label="Firstname"
            variant="outlined"
            value={wishlistItemchild.firstname}
            onChange={(e) => setFirstname(e.target.value)}
            sx={{ m: 2 }}
          />
          <TextField
            label="Lastname"
            variant="outlined"
            value={props.item.child.lastname}
            onChange={(e) => setLastname(e.target.value)}
            sx={{ m: 2 }}
          />
          <TextField
            id="outlined-number"
            label="Age"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            value={props.item.child.age}
            onChange={(e) => setAge(e.target.value)}
            sx={{ m: 2 }}
          />
          <TextField
            id="outlined-number"
            label="Year"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            value={props.item.year}
            onChange={(e) => setYear(e.target.value)}
            sx={{ m: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleUpdate}>Add</Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
}
