import React, { useState, useEffect } from "react";
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from "@mui/material";
import {
  deleteWishlist,
  getAllWishlists,
  postWishlist,
  updateWishlist,
} from "../../api/wishlistApi.js";

export default function UpdateDialog(props) {
  let [open, setOpen] = React.useState(false);
  let [firstname, setFirstname] = React.useState("");
  let [lastname, setLastname] = React.useState("");
  let [age, setAge] = React.useState();
  let [year, setYear] = React.useState();
  let [child, setChild] = React.useState({
    firstname: "",
    lastname: "",
    age: 0,
  });

  const handleUpdateWishlist = () => {
    console.log(props.item);
    let updatedChild = {
      firstname: firstname,
      lastname: lastname,
      age: age,
    };

    updateWishlist(props.item._id, year, updatedChild, props.item.items);
    window.location.reload();
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleUpdate = () => {
    handleUpdateWishlist();
    handleClose();
  };

  let itemChild = props.item.child;

  return (
    <React.Fragment>
      <Button variant="outlined" sx={{ m: 2 }} onClick={handleClickOpen}>
        Update Wishlist
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here.
            We will send updates occasionally.
          </DialogContentText>
          <TextField
            label="Firstname"
            variant="outlined"
            value={itemChild.firstname}
            onChange={(e) => setChild({ ...child, firstname: e.target.value })}
            sx={{ m: 2 }}
          />
          <TextField
            label="Lastname"
            variant="outlined"
            value={itemChild.lastname}
            onChange={(e) => setChild({ ...child, lastname: e.target.value })}
            sx={{ m: 2 }}
          />
          <TextField
            id="outlined-number"
            label="Age"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            value={itemChild.age}
            onChange={(e) => setAge(e.target.value)}
            sx={{ m: 2 }}
          />
          <TextField
            id="outlined-number"
            label="Year"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            value={props.item.year}
            onChange={(e) => setYear(e.target.value)}
            sx={{ m: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleUpdate}>Add</Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
}
