import * as React from "react";
import TextField from "@mui/material/TextField/index.js";
import Button from "@mui/material/Button/index.js";
import Dialog from "@mui/material/Dialog/index.js";
import DialogActions from "@mui/material/DialogActions/index.js";
import DialogContent from "@mui/material/DialogContent/index.js";
import DialogContentText from "@mui/material/DialogContentText/index.js";
import DialogTitle from "@mui/material/DialogTitle/index.js";

import {
  deleteWishlist,
  getAllWishlists,
  postWishlist,
} from "../../api/wishlistApi.js";

export default function PostDialog() {
  const [open, setOpen] = React.useState(false);
  const [year, setYear] = React.useState(2023);
  const [child, setChild] = React.useState({
    firstname: "",
    lastname: "",
    age: 0,
  });
  const [items, setItems] = React.useState([]);

  const [itemName, setItemName] = React.useState("");
  const [itemPrice, setItemPrice] = React.useState();

  const addItem = () => {
    if (itemName && itemPrice) {
      // Create a new item object
      const newItem = {
        name: itemName,
        price: itemPrice,
      };

      // Update the items state with the new item
      setItems([...items, newItem]);

      // Clear the input fields
      setItemName("");
      setItemPrice("");
    }
  };

  const handleAddWishlist = () => {
    const formData = {
      year: year,
      child: { ...child },
      items: [...items],
    };
    postWishlist(formData);
    handleClose();
    window.location.reload();
  };

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const handleAddItem = () => {
    setItems((prevItems) => [...prevItems, { name: "", currentprice: 0 }]);
  };
  const handleRemoveItem = (id) => {
    setItems((prevItems) => prevItems.filter((item) => item._id !== id));
  };

  const handleItemChange = (id, field, value) => {
    setItems((prevItems) =>
      prevItems.map((item) =>
        item._id === id ? { ...item, [field]: value } : item
      )
    );
  };

  const handleAdd = () => {
    handleAddWishlist();
  };

  return (
    <React.Fragment>
      <Button variant="contained" sx={{ m: 2 }} onClick={handleClickOpen}>
        Add Wishlist
      </Button>
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Add Wishlist</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please fill out the form below to add a wishlist.
          </DialogContentText>
          <TextField
            label="Year"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Child's First Name"
            value={child.firstname}
            onChange={(e) => setChild({ ...child, firstname: e.target.value })}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Child's Last Name"
            value={child.lastname}
            onChange={(e) => setChild({ ...child, lastname: e.target.value })}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Child's Age"
            value={child.age}
            type="number"
            onChange={(e) =>
              setChild({ ...child, age: parseInt(e.target.value) })
            }
            fullWidth
            margin="normal"
          />








          <div>
            {/* Form to input item details */}
            <div>
              <label>
                Name:
                <input
                  type="text"
                  value={itemName}
                  onChange={(e) => setItemName(e.target.value)}
                />
              </label>
              <label>
                Price:
                <input
                  type="text"
                  value={itemPrice}
                  onChange={(e) => setItemPrice(e.target.value)}
                />
              </label>
              <button onClick={addItem}>Add Item</button>
            </div>

            {/* Table to display items */}
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Price</th>
                </tr>
              </thead>
              <tbody>
                {items.map((item, index) => (
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>{item.price}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <Button onClick={handleAddItem}>Add Item</Button>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleAdd}>Add</Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
  );
}
