import React, { useEffect, useState } from "react";
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete.js";
import { Link, Route, useNavigate } from "react-router-dom";
import { getAllWishlists } from "../../api/wishlistApi.js";
import axios from "axios";
const url = "http://localhost:8000/api";

class WishlistList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    this.getAllWishlists();
  }

  getAllWishlists = async () => {
    try {
      const response = await axios.get(`${url}/getAll`);
      console.log(response.data);
      setItems(response.data); // Update the state with the fetched data
      this.setState({ data: response.data });
    } catch (error) {
      console.error("Error fetching wishlists:", error.message);
    }
  };

  handleDelete = (itemId) => {
    setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
  };

  navigate = useNavigate();

  handleRowClick = (itemId) => {
    // Redirect to the details page or another list page
    navigate(`/${itemId}`);
  };
  render() {
    return (
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Action</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {wishlistItems.map((item) => (
              <TableRow key={item._id} onClick={() => handleRowClick(item._id)}>
                <TableCell>{item.name}</TableCell>
                <TableCell>{item.age}</TableCell>
                <TableCell>
                  <IconButton
                    aria-label="delete"
                    onClick={() => handleDelete(item._id)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      // <List>
      //   {items.map((item) => (
      //     <ListItem key={item.id}>
      //       <ListItemText
      //         primary={`${item.firstname} ${item.lastname} | ${item.year}`}
      //         // Add more ListItemText properties as needed
      //       />
      //       <ListItemSecondaryAction>
      //         <IconButton
      //           edge="end"
      //           aria-label="delete"
      //           onClick={() => handleDelete(item.id)}
      //         >
      //           <DeleteIcon />
      //         </IconButton>
      //       </ListItemSecondaryAction>
      //     </ListItem>
      //   ))}
      // </List>
    );
  }
}

export default WishlistList;
