    super(props);
    useLocation()

    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    let wishlists = getAllWishlists();
    new Promise((resolve) => {
      return resolve(wishlists);
    }).then((res) => this.setState({ data: res }));
  }

  render() {
    const data = this.state.data;

    return (
      <TableContainer component={Paper}>
        <PostDialog />
